mapper.update:
UPDATE course c SET `time` =(
    SELECT SUM(`time`) FROM section s WHERE s.course_id = #{courseId})
    WHERE c.id = #{courseId};


update course
    set `sort` = (`sort` - 1 )
where
    `sort` >= #{oldSort} and `sort` <= #{newSort}
    and `id`!= #{id}


update course
    set `sort` = (`sort` + 1 )
where
    `sort` >= #{newSort} and `sort` <= #{oldSort}
    and `id`!= #{id}
